css_structure:
#element_onlycodez103 {
  background-image: url("{{image.backgroundImage}}");
  background-size: cover;
  background-position: center;
  background-attachment: fixed;
  width: 100vw;
  height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-sizing: border-box;
  padding: 0;
  overflow: hidden;
}

#element_onlycodez103::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, {{color.overlayOpacity}});
  z-index: 1;
}

.faqs-title {
  font-size: 40px;
  color: {{color.titleColor}};
  text-align: center;
  width: 100%;
  margin-bottom: 30px;
  z-index: 2;
}

.faq-question-container {
  width: 80%;
  max-width: 800px;
  padding: 15px 20px;
  border-radius: 5px;
  margin-bottom: 15px;
  cursor: pointer;
  transition: all 0.5s ease-in-out;
  background-color: {{color.questionBackgroundColor}};
  text-align: center;
  z-index: 2;
}

.faq-question {
  font-size: 18px;
  font-weight: 400;
  margin: 0;
  color: {{color.questionTextColor}};
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
  transition: color 0.3s ease;
}

.faq-description {
  max-height: 0;
  font-weight: 400;
  margin-top: 0;
  margin-bottom: 0;
  overflow: hidden;
  transition: max-height 0.5s ease-in-out, opacity 0.5s ease-in-out, padding 0.5s ease-in-out;
  padding: 0 10px;
  font-size: 14px;
  color: {{color.answerTextColor}};
  opacity: 0;
  text-align: justify;
}

.faq-question-container.active .faq-description {
  max-height: 1000px;
  opacity: 1;
  padding: 10px;
}

.faq-question-container.active .faq-question {
  font-weight: bold;
}

@media (max-width: 768px) {
  .faq-question-container {
    width: 90%;
    padding: 10px 15px;
  }

  .faqs-title {
    font-size: 28px;
  }

  .faq-question {
    font-size: 16px;
  }

  .faq-description {
    font-size: 12px;
  }
}

#faqContent {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
}
